-- ========================================
-- VERIFICAR INSERCIÓN CUARTA OLEADA
-- ========================================

SELECT COUNT(*) as total_carreras_cuarta_oleada FROM races;
SELECT COUNT(DISTINCT city) as ciudades_unicas FROM races;
SELECT COUNT(DISTINCT province) as provincias_unicas FROM races;
SELECT source_platform, COUNT(*) as carreras_por_fuente FROM races GROUP BY source_platform ORDER BY carreras_por_fuente DESC;
SELECT 
    CASE 
        WHEN registration_price < 15 THEN 'Económicas (< 15€)'
        WHEN registration_price BETWEEN 15 AND 30 THEN 'Normales (15-30€)'
        WHEN registration_price > 30 THEN 'Premium (> 30€)'
    END as categoria_precio,
    COUNT(*) as cantidad
FROM races 
GROUP BY 
    CASE 
        WHEN registration_price < 15 THEN 'Económicas (< 15€)'
        WHEN registration_price BETWEEN 15 AND 30 THEN 'Normales (15-30€)'
        WHEN registration_price > 30 THEN 'Premium (> 30€)'
    END
ORDER BY cantidad DESC;